import ping from '../images/ping.png'
import Card from './Card';
import { api } from '../utils/Api';
import { useState, useEffect, useContext } from 'react';
import { currentContext } from '../contexts/CurrentUserContext';

function Main({
  onEditProfile, 
  onAddPlace, 
  onEditAvatar,
  onCardClick
  }) {
    const currentMain = useContext(currentContext);
    console.log(currentMain.currentUser)
    
    //const [userName, setUserName] = useState('');
    //const [userDescription, setUserDescription] = useState('');
    //const [userAvatar, setUserAvatar] = useState('');
    const [cards, setCards] = useState([])
   // useEffect(() => {
   //   api.getUserInfo()
   //   .then((item) => {
   //     setUserName(item.name)
   //     setUserDescription(item.about)
   //     setUserAvatar(item.avatar)
   //   }).catch((err) => {
   //     console.error(err)
   //   })
   // }, [])
    useEffect(() => {
      api.getInitialCards()
      .then((res) => {
        setCards(res)
      }).catch((err) => {
        console.error(err)
      })
    }, [])
  return(
    <main className="content">
      <section className="profile">
        <div className="profile__content" onClick={onEditAvatar}>
          <img className="profile__image" src={currentMain.currentUser.avatar} alt="Жак-ИвКусто" />
          <img className="profile__ping" src={ping} alt="Ручка" />
        </div>
        <div className="profile__info">
          <div className="profile__save">
            <h1 className="profile__name">{currentMain.currentUser.name}</h1>
            <button className="profile__button" type="button" onClick={onEditProfile}></button>
          </div>
          <p className="profile__job">{currentMain.currentUser.about}</p>
        </div>
        <button className="profile__add" type="button" onClick={onAddPlace}></button>
      </section>
      <section className="elements">
          {cards.map((item) => (
            <Card 
              src={item.link}
              like={item.likes.length}
              title={item.name}
              alt={item.name}
              onClick={onCardClick}
              key={item._id}
            />
          ))}
      </section>
    </main>
  )
}
export default Main;
