import ping from '../images/ping.png'
import { api } from '../utils/Api';
import { useState } from 'react';
import { useEffect } from 'react';

function Main({
  onEditProfile, 
  onAddPlace, 
  onEditAvatar}) {
    const [userName, setUserName] = useState('');
    const [userDescription, setUserDescription] = useState('');
    const [userAvatar, setUserAvatar] = useState('');
    const [cards, setCards] = useState([])
    useEffect(() => {
      api.getUserInfo()
      api.getInitialCards()
      .then((item) => {
        setUserName(item.name)
        setUserDescription(item.about)
        setUserAvatar(item.avatar)
      }).catch((err) => {
        console.error(err)
      })
    }, [])
  return(
    <main className="content">
      <section className="profile">
        <div className="profile__content" onClick={onEditAvatar}>
          <img className="profile__image" src={userAvatar} alt="Жак-ИвКусто" />
          <img className="profile__ping" src={ping} alt="Ручка" />
        </div>
        <div className="profile__info">
          <div className="profile__save">
            <h1 className="profile__name">{userName}</h1>
            <button className="profile__button" type="button" onClick={onEditProfile}></button>
          </div>
          <p className="profile__job">{userDescription}</p>
        </div>
        <button className="profile__add" type="button" onClick={onAddPlace}></button>
      </section>
      <template className="element__template">
        <div className="element">
          <img className="element__image" />
          <button className="element__reset" type="reset"></button>
            <div className="element__info">
              <p className="element__text"></p>
                <div className="element__like">
                  <button className="element__button" type="button"></button>
                  <span className="element__like_button">0</span>
                </div>
            </div>
        </div>
      </template>
    </main>
  )
}
export default Main;
