import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import PopupWithForm from './PopupWithForm';
import ImagePopup from './ImagePopup';
import { useState, useEffect } from 'react';
import { api } from '../utils/Api';
import { CurrentContext } from '../contexts/CurrentUserContext';
import EditProfilePopup from './EditProfilePopup';

function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false)
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false)
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false)
  const [selectedCard, setSelectedCard] = useState(null)
  const [currentUser, setCurrentUser] = useState(false);
  const [cards, setCards] = useState([])

  useEffect(() => {
    api.getInitialCards()
    .then((res) => {
      setCards(res)
    }).catch((err) => {
      console.error(err)
    })
  }, [])

  useEffect(() => {
    api.getUserInfo()
      .then((res) => {
        setCurrentUser(res)
      }).catch((err) => {
        console.error(err)
      })
  }, [])
  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true)
  }
  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true)
  }
  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true)
  }
  function closeAllPopups() {
    setIsEditProfilePopupOpen(false)
    setIsAddPlacePopupOpen(false)
    setIsEditAvatarPopupOpen(false)
    setSelectedCard(null)
  }
  function handleCardClick(card) {
    setSelectedCard(card)
  }
  function handleUpdateUser(item) {
    api.setUserInfo(item.name, item.about).then((res) => {
      setCurrentUser(res)
      closeAllPopups()
    }).catch((err) => {
      console.error(err)
    })
  }
  function handleCardLike(likes, id) {
    //const isLiked = likes.some(i => i._id === currentUser._id);
    api.likeCard(id, !isLiked).then((newCard) => {
      setCards((state) => state.map((c) => c._id === id ? newCard : c));
    }).catch((err) => {
      console.error(err)
    });
    if(likes.some(i => i._id === currentUser._id)) {
      api.likeCard(id).then((newCard) => {
        setCards((state) => state.map((c) => c._id === id ? newCard : c));
      }).catch((err) => {
        console.error(err)
      });
    }
    //api.deleteLikeCard(id, isLiked).then((newCard) => {
    //  setCards((state) => state.map((c) => c._id === id ? newCard : c));
    //}).catch((err) => {
    //  console.error(err)
    //});
  }
  return (
    <>
      <CurrentContext.Provider value={currentUser}>
        <Header />
        <Main 
          card={cards}
          onCardLike={handleCardLike}
          onCardClick={handleCardClick}
          onEditProfile={handleEditProfileClick}
          onAddPlace={handleAddPlaceClick}
          onEditAvatar={handleEditAvatarClick}
        />
        <Footer />
        <EditProfilePopup
          isOpen={isEditProfilePopupOpen}
          onClose={closeAllPopups}
          onUpdateUser={handleUpdateUser}
          />
        <PopupWithForm 
          isOpen={isAddPlacePopupOpen}
          onClose={closeAllPopups}
          name='card'
          title='Новое место'
          buttonText='Создать'
          children={
            <>
            <input minLength="2" maxLength="30" className="popup__info popup__info_type_mesto" type="text" name="mesto" placeholder="Название" required />
            <span id="mesto-error" className="popup__error"></span>
            <input className="popup__info popup__info_type_link" type="url" name="link" placeholder="Ссылка на картинку" required />
            <span id="link-error" className="popup__error"></span>
            </>
          } />
        <PopupWithForm
          name='delete'
          title='Вы уверены?'
          buttonText='Да'
          children={
            <>
            <p className="popup__text">Вы уверены?</p>
            </>
          } />
        <PopupWithForm
          isOpen={isEditAvatarPopupOpen}
          onClose={closeAllPopups}
          name='avatar'
          title='Обновить аватар'
          buttonText='Сохранить'
          children={
            <>
            <input className="popup__info popup__info_type_avatar" type="url" name="avatar" placeholder="Ссылка на смену аватара" required />
            <span id="avatar-error" className="popup__error"></span>
            </>
          } />
        <ImagePopup 
          card={selectedCard}
          onClose={closeAllPopups}
          />

      </CurrentContext.Provider>
    </>
  );
}

export default App;
