import { useContext } from "react";
import { currentContext } from "../contexts/CurrentUserContext";


function Card({src, alt, title, like, onClick}) {
  const currentCardContext = useContext(currentContext);
  const cards = currentCardContext.currentCard.forEach(item => {
    const cardsName = item.name
    const cardsLink = item.link
  })
  

  return(
    <div className="element">
      <img className="element__image" src={cards} alt={cards} onClick={() => onClick({src, alt})}/>
      <button className="element__reset" type="reset"></button>
        <div className="element__info">
          <p className="element__text">{title}</p>
            <div className="element__like">
              <button className="element__button" type="button"></button>
              <span className="element__like_button">{like}</span>
            </div>
        </div>
    </div>
    
  )
}
export default Card;
