const TOKEN_KEY = 'jwt';

export const setToken = (jwt) => {
  localStorage.setItem(TOKEN_KEY, jwt);
}

export const getToken = () => localStorage.getItem(TOKEN_KEY);

export const removeToken = () => {
  localStorage.removeItem(TOKEN_KEY)
}
