import App from './App';

function Main() {
  function handleEditAvatarClick() {
    const avatar = document.querySelector('.popup_type_avatar');
    document.addEventListener('click', () => {
      avatar.classList.add('popup_opened')
    })
  }
  function handleEditProfileClick() {
    const profile = document.querySelector('.popup_type_information');
    document.addEventListener('click', () => {
      profile.classList.add('popup_opened')
    })
  }
  function handleAddPlaceClick() {
    const addCard = document.querySelector('.popup_type_card');
    document.addEventListener('click', () => {
      addCard.classList.add('popup_opened')
    })
  }
  return(
    <main className="content">
      <section className="profile">
        <div className="profile__content" onClick={handleEditAvatarClick}>
          <img className="profile__image" src="<%=require('./images/Jak.png')%>" alt="Жак-ИвКусто" />
          <img className="profile__ping" src="<%=require('./images/ping.png')%>" alt="Ручка" />
        </div>
        <div className="profile__info">
          <div className="profile__save">
            <h1 className="profile__name">Жак-Ив Кусто</h1>
            <button className="profile__button" type="button" onClick={handleEditProfileClick}></button>
          </div>
          <p className="profile__job">Исследователь океана</p>
        </div>
        <button className="profile__add" type="button"></button>
      </section>
      <section className="elements"></section>
    </main>
  )
}
export default Main;
